var http = require('http');

function Utilities(){

}

Utilities.prototype.getHora = function (callBack){
	var options = {
	  host: 'api.timezonedb.com',
	  port: 80,
	  path: '/?zone=America/Bogota&format=json&key=6HET1209IX0B',
	  method: 'Get'
	};

	http.request(options, function(res) {
		res.setEncoding('utf8');
		res.on('data', function(result){
			if (result) {
				currentDate = new Date();
				currentDate.getSeconds(result.timestamp + 1000 * 3600);
				callBack(result.timestamp);
			};
		});
	}).end();
};

var utilities = module.exports = new Utilities();